import React, {useState} from 'react';
import Nav from "./components/navbar"
import Footer from "./components/footer"
import Content from "./components/content"
import {muscles, mainExercises} from "./store"


function App() {
  let [selectedCategory, setSelectedCategory] = useState(mainExercises)
  let [exercises, setExercises] = useState(main)

  let sortedEx = ()=> {

    let sortedExercise =  exercises.reduce((exercises, exercise) => {
      let {muscles} = exercise;
      exercises[muscles] = exercises[muscles] ? [...exercises[muscles], exercise]: exercises[muscles] = [exercise]
        return exercises
      }, {})
      return Object.entries(sortedExercise)

  }
  let handleChange = () => {
    setSelectedCategory 
  }


  return (
    <div>
      <Nav />   
      <Content exercises = {sortedEx()}/>
      <Footer muscles = {muscles} value = {0} handleChange = {handleChange} />
    </div>
  )

      }

export default App;
