import React, {useState, useEffect} from 'react';
import Nav from "./components/navbar"
import Footer from "./components/footer"
import Content from "./components/content"
import {muscles, mainExercises} from "./store"



function App() {
  let [selectedMuscles, setSelectedMuscles] = useState("all")
  let [exercises, setExercises] = useState(mainExercises)

  let sortedEx = ()=> {

    let sortedExercise =  exercises.reduce((exercises, exercise) => {
    let {muscles} = exercise;
      exercises[muscles] = exercises[muscles] ? [...exercises[muscles], exercise]: exercises[muscles] = [exercise]
        return exercises
      }, {})
      return Object.entries(sortedExercise)

  }

  let exercisesArray = sortedEx()

  let handleChange = (category, indx) => {
    setSelectedMuscles(category)
    indx === 0 ? setSelectedCategory("") : setSelectedCategory(category)
  }

  let [selectedCategory, setSelectedCategory] = useState("")
  let [selectedSubCategory, setSelctedSubCategory] = useState(exercisesArray[0][1][0])

  setExercises(exercisesArray)

  let handleSelect = x => setSelctedSubCategory(x)
  return (
    <div>
      <Nav />   
      <Content exercises = {exercises} selectedSubCategory = {selectedSubCategory} handleSelect = {handleSelect} />
      <Footer category = {selectedMuscles} muscles = {muscles} handleChange = {handleChange} />
    </div>
  )

      }

export default App;
