import React, {useState} from 'react';
import Nav from "./components/navbar"
import Footer from "./components/footer"
import Content from "./components/content"
import {muscles, exercises} from "./store"


function App() {
  let [selectedCategory, setSelectedCategory] = useState({})
  let [exercises, setExercises] = useState(exercises)

  let sortedEx = ()=> {

    let sortedExercise =  exercises.reduce((exercises, exercise) => {
      let {muscles} = exercise;
      exercises[muscles] = exercises[muscles] ? [...exercises[muscles], exercise]: exercises[muscles] = [exercise]
        return exercises
      }, {})
      return Object.entries(sortedExercise)

  }



  return (
    <div>
      <Nav />   
      <Content mainExercises = {sortedEx()}/>
      <Footer muscles = {muscles} />
    </div>
  )

      }

export default App;
