import React, {useState} from 'react';
import Nav from "./components/navbar"
import Footer from "./components/footer"
import Content from "./components/content"
import {muscles, mainExercises} from "./store"
import { exec } from 'child_process';


function App() {
  let [selectedCategory, setSelectedCategory] = useState(mainExercises)
  let [selectedMuscles, setSelectedMuscles] = useState("arms")
  let [exercises, setExercises] = useState(mainExercises)

  let sortedEx = ()=> {

    let sortedExercise =  exercises.reduce((exercises, exercise) => {
    let {muscles} = exercise;
      exercises[muscles] = exercises[muscles] ? [...exercises[muscles], exercise]: exercises[muscles] = [exercise]
        return exercises
      }, {})
      return Object.entries(sortedExercise)

  }

  let exercisesArray = sortedEx()
  let handleChange = (category, indx) => {
    setSelectedMuscles(category)
    indx === 0 ? setSelectedCategory(exercisesArray) : setSelectedCategory(exercisesArray[indx-1])
  }



  return (
    <div>
      <Nav />   
      <Content exercises = {exercisesArray}/>
      <Footer category = {selectedMuscles} muscles = {muscles} handleChange = {handleChange} />
    </div>
  )

      }

export default App;
